                    <span id="engineeringValue">5</span>
                </div>
                <div class="stat-row">
                    <span>Leadership</span>
                    <div class="stat-bar"><div id="leadershipStat" class="stat-fill" style="width: 50%"></div></div>
                    <span id="leadershipValue">5</span>
                </div>
                <div class="stat-row">
                    <span>Reputation</span>
                    <div class="stat-bar"><div id="reputationStat" class="stat-fill" style="width: 50%"></div></div>
                    <span id="reputationValue">5</span>
                </div>
