            <div class="location-info">
                <div class="location-name" id="locationName"></div>
                <div class="weather-info" id="weatherInfo"></div>
                <div id="locationDescription"></div>
            </div>

            <div class="stats">
                <h3 class="section-title">Resources</h3>
                <div class="stat-row">
                    <span>Coins</span>
