                <div class="stat-row">
                    <span>Supplies</span>
                    <div class="stat-bar"><div id="suppliesStat" class="stat-fill" style="width: 80%"></div></div>
                    <span id="suppliesValue">80</span>
                </div>
            </div>

            <div class="story-text" id="storyText"></div>

            <div class="choices" id="choices"></div>

            <div class="inventory">
                <h3>Inventory</h3>
