    <div id="notification" class="notification"></div>

    <script>
        // Game State
        let gameState = {
            character: null,
            currentLocation: null,
            currentQuest: null,
            day: 1,
            gameEnded: false
        };

        // Procedural Generation
        const cityNames = [
            "Brasshaven", "Cogwheel Junction", "Steamport Alpha", "Victorian Vale",
            "Copperfield Heights", "Geargrind Central", "Aethermoor Central", "Piston Point"
        ];

        const locations = [
            {
                name: "The Brass Lantern Tavern",
                type: "tavern",
                description: "A bustling tavern filled with the clink of ale mugs and the murmur of conspirators. Steam pipes hiss overhead while patrons discuss the latest news.",
                weather: "warm and stuffy from the nearby furnaces",
                encounters: ["tavernQuest", "information", "gambling"]
            },
            {
                name: "Professor Cogwright's Workshop",
                type: "workshop",
                description: "A cluttered workshop filled with gleaming brass instruments, steam-powered contraptions, and the acrid smell of hot metal.",
                weather: "smells of oil and steam",
                encounters: ["inventionQuest", "crafting", "parts"]
            },
            {
                name: "Newspaper Alley",
                type: "news",
                description: "Vendors hawk the latest editions of the Aethermoor Gazette while newsboys shout headlines about airship disasters and political scandals.",
                weather: "fresh with the scent of printing ink",
                encounters: ["newsQuest", "information", "witnesses"]
            },
            {
                name: "The Steam Quarter",
                type: "market",
                description: "A maze of narrow streets lined with steam-powered vendors, selling everything from clockwork toys to pneumatic messages.",
                weather: "hazy with steam from countless engines",
                encounters: ["tradeQuest", "shopping", "supplies"]
            },
            {
                name: "Airship Docks",
                type: "docks",
                description: "Massive airships are tethered to brass posts while cargo crews load goods under the watchful eye of dockworkers.",
                weather: "windy with the smell of coal smoke",
                encounters: ["shippingQuest", "captainQuest", "travel"]
            },
            {
                name: "Scotland Yard - Aethermoor Division",
                type: "police",
                description: "A imposing building of brass and stone where detectives investigate the city's most perplexing mysteries.",
                weather: "serious and somber atmosphere",
                encounters: ["detectiveQuest", "cases", "evidence"]
            }
        ];

        const vocabulary = {
            aristocratic: ["quite", "indeed", "utterly", "absolutely", "perfectly"],
            common: ["rather", "quite", "rather", "might I say", "I dare say"],
            exclamations: ["By Jove!", "Good heavens!", "Extraordinary!", "Indeed!", "Most curious!"],
            formal: ["I humbly request", "Might I suggest", "Allow me to propose", "I would venture to say"]
        };

        const quests = {
            mysteryMurder: {
                name: "The Clockwork Murder",
                description: "A wealthy industrialist has been found dead in his study, killed by a mysterious clockwork mechanism.",
                requiredClass: "detective",
                reward: 100,
                statReward: { investigation: 3, reputation: 2 }
            },
            missingInventor: {
                name: "The Vanishing Genius",
                description: "Professor Blackwood has disappeared, leaving only strange blueprints behind.",
                requiredClass: "inventor",
                reward: 80,
                statReward: { engineering: 3, reputation: 2 }
            },
            airshipSabotage: {
                name: "Sabotage in the Skies",
                description: "Airships have been mysteriously sabotaged, and cargo is disappearing.",
                requiredClass: "captain",
                reward: 90,
                statReward: { leadership: 3, reputation: 2 }
            },
            stolenSteamCore: {
                name: "The Stolen Steam Core",
                description: "The city's main steam core has been stolen, threatening to freeze the entire city.",
                requiredClass: "any",
                reward: 150,
                statReward: { reputation: 5 }
            }
        };

        // Character Classes
        const characterClasses = {
            detective: {
                name: "Detective",
                description: "Master of deduction",
                stats: { investigation: 7, engineering: 3, leadership: 4, reputation: 5 },
                startingGear: ["Magnifying Glass", "Notebook", "Evidence Kit"],
                specialAbility: "Keen Observation - Can examine clues more thoroughly"
            },
            inventor: {
                name: "Inventor",
                description: "Master of engineering",
                stats: { investigation: 4, engineering: 7, leadership: 4, reputation: 4 },
                startingGear: ["Tool Kit", "Blueprint Sketches", "Spare Parts"],
                specialAbility: "Field Engineering - Can craft solutions on the spot"
            },
            captain: {
                name: "Airship Captain",
                description: "Master of the skies",
                stats: { investigation: 4, engineering: 4, leadership: 7, reputation: 6 },
                startingGear: ["Airship", "Navigation Charts", "Crew Manifest"],
                specialAbility: "Command Presence - Can rally support from NPCs"
            }
        };

        // UI Elements
        const screens = {
            characterCreation: document.getElementById('characterCreation'),
            mainGame: document.getElementById('mainGame'),
            gameOver: document.getElementById('gameOver')
        };

        const elements = {
            characterName: document.getElementById('characterName'),
            characterCards: document.querySelectorAll('.character-card'),
            startButton: document.getElementById('startGame'),
            displayName: document.getElementById('displayName'),
            displayClass: document.getElementById('displayClass'),
            locationName: document.getElementById('locationName'),
            weatherInfo: document.getElementById('weatherInfo'),
            locationDescription: document.getElementById('locationDescription'),
            storyText: document.getElementById('storyText'),
            choices: document.getElementById('choices'),
            inventoryGrid: document.getElementById('inventoryGrid'),
            coins: document.getElementById('coins'),
            suppliesValue: document.getElementById('suppliesValue'),
            suppliesStat: document.getElementById('suppliesStat'),
            investigationStat: document.getElementById('investigationStat'),
            engineeringStat: document.getElementById('engineeringStat'),
            leadershipStat: document.getElementById('leadershipStat'),
            reputationStat: document.getElementById('reputationStat'),
            investigationValue: document.getElementById('investigationValue'),
            engineeringValue: document.getElementById('engineeringValue'),
            leadershipValue: document.getElementById('leadershipValue'),
            reputationValue: document.getElementById('reputationValue'),
            gameOverMessage: document.getElementById('gameOverMessage'),
            endStats: document.getElementById('endStats'),
            notification: document.getElementById('notification')
        };

        // Utility Functions
        function showScreen(screenName) {
            Object.values(screens).forEach(screen => screen.classList.remove('active'));
            screens[screenName].classList.add('active');
        }

        function randomChoice(array) {
            return array[Math.floor(Math.random() * array.length)];
        }

        function rollDice(sides = 20) {
            return Math.floor(Math.random() * sides) + 1;
        }

        function speakVictorian(text, formality = "common") {
            const vocab = vocabulary[formality];
            let sentences = text.split('.');
            sentences = sentences.map(sentence => {
                if (Math.random() < 0.3) {
                    sentence = randomChoice(vocab.formal) + " " + sentence.toLowerCase();
                }
                if (Math.random() < 0.2) {
                    sentence += " " + randomChoice(vocab.exclamations);
                }
                return sentence.trim();
            });
            return sentences.join('. ') + (text.endsWith('.') ? '' : '.');
        }

        function updateNotification(message) {
            elements.notification.textContent = message;
            elements.notification.classList.add('show');
            setTimeout(() => {
                elements.notification.classList.remove('show');
            }, 3000);
        }

        function updateStats() {
            const stats = gameState.character.stats;
            elements.investigationStat.style.width = (stats.investigation * 10) + '%';
            elements.engineeringStat.style.width = (stats.engineering * 10) + '%';
            elements.leadershipStat.style.width = (stats.leadership * 10) + '%';
            elements.reputationStat.style.width = (stats.reputation * 10) + '%';

            elements.investigationValue.textContent = stats.investigation;
            elements.engineeringValue.textContent = stats.engineering;
            elements.leadershipValue.textContent = stats.leadership;
            elements.reputationValue.textContent = stats.reputation;
        }

        function updateInventory() {
            elements.inventoryGrid.innerHTML = '';
            gameState.character.inventory.forEach(item => {
                const itemDiv = document.createElement('div');
                itemDiv.className = 'inventory-item';
                itemDiv.textContent = item;
                elements.inventoryGrid.appendChild(itemDiv);
            });
        }

        function updateResources() {
            elements.coins.textContent = gameState.character.coins;
            elements.suppliesValue.textContent = gameState.character.supplies;
            elements.suppliesStat.style.width = gameState.character.supplies + '%';
        }

        // Game Initialization
        function createCharacter(name, classType) {
            const classData = characterClasses[classType];
            return {
                name: name,
                class: classType,
                className: classData.name,
                stats: { ...classData.stats },
                inventory: [...classData.startingGear],
                coins: 50,
                supplies: 80,
                reputation: classData.stats.reputation
            };
        }

        function generateLocation() {
            const location = { ...randomChoice(locations) };
            return location;
        }

        function generateLocationText(location, character) {
            const openings = [
                `You arrive at ${location.name}`,
                `Thy travels bring thee to ${location.name}`,
                `${location.name} beckons thee forward`,
                `Upon entering ${location.name}`
            ];

            const characterThoughts = {
                detective: "Your keen eyes survey the area for clues and details",
                inventor: "Your engineering mind automatically catalogs the mechanisms",
                captain: "Your leadership instincts assess the situation"
            };

            const opening = randomChoice(openings);
            const thought = characterThoughts[character.class];
            const fullText = `${opening}, ${location.description} The air is ${location.weather}. ${thought}.`;

            return speakVictorian(fullText);
        }

        // Event Generation
        function generateEvent() {
            const events = [];

            // Quest Event
            if (!gameState.currentQuest && Math.random() < 0.6) {
                const availableQuests = Object.values(quests).filter(q => 
                    q.requiredClass === gameState.character.class || 
                    q.requiredClass === 'any' ||
                    Math.random() < 0.3
                );
                if (availableQuests.length > 0) {
                    events.push({
                        type: 'quest',
                        data: randomChoice(availableQuests)
                    });
                }
            }

            // Trading Event
            if (Math.random() < 0.4) {
                events.push({
                    type: 'trade',
                    data: {}
                });
            }

            // Random Encounter
            if (Math.random() < 0.3) {
                events.push({
                    type: 'encounter',
                    data: generateRandomEncounter()
                });
            }

            // Rest Event
            if (Math.random() < 0.2) {
                events.push({
                    type: 'rest',
                    data: {}
                });
            }

            return events.length > 0 ? randomChoice(events) : {
                type: 'description',
                data: { text: "You take a moment to observe your surroundings." }
            };
        }

        function generateRandomEncounter() {
            const encounters = [
                {
                    text: "A street urchin runs up to you, claiming to have seen something suspicious.",
                    choices: [
                        {
                            text: "Listen to the child's tale",
                            consequence: () => {
                                gameState.character.reputation++;
                                updateStats();
                                updateNotification("The child's trust increases your reputation!");
                                return "The child thanks you for believing them and points you toward the old mill.";
                            }
                        },
                        {
                            text: "Ignore the child",
                            consequence: () => {
                                gameState.character.reputation--;
                                updateStats();
                                updateNotification("Your reputation suffers for your coldness.");
                                return "You walk away, missing a potential lead.";
                            }
                        }
                    ]
                },
                {
                    text: "You notice a suspicious figure following you through the foggy streets.",
                    choices: [
                        {
                            text: "Confront the follower",
                            consequence: () => {
                                const roll = rollDice(20);
                                if (roll > 10) {
                                    gameState.character.coins += 25;
                                    updateResources();
                                    updateNotification("You successfully confront them and receive a reward for exposing a spy!");
                                    return "The 'follower' was actually a grateful merchant who wanted to test your警觉性. They reward you for your bravery.";
                                } else {
                                    gameState.character.supplies -= 10;
                                    updateResources();
                                    updateNotification("The confrontation goes poorly, and you lose supplies.");
                                    return "The confrontation escalates, and you narrowly escape, but not without losing some supplies.";
                                }
                            }
                        },
                        {
                            text: "Attempt to lose them",
                            consequence: () => {
                                const roll = rollDice(20);
                                if (roll > 12) {
                                    updateNotification("You successfully shake your tail!");
                                    return "You expertly lose the follower in the maze-like streets.";
                                } else {
                                    gameState.character.supplies -= 15;
                                    updateResources();
                                    updateNotification("You lose precious supplies while escaping!");
                                    return "The follower is persistent, and you lose precious time and supplies escaping.";
                                }
                            }
                        }
                    ]
                }
            ];

            return randomChoice(encounters);
        }

        // Choice Handling
        function presentChoices(choices, currentEvent) {
            elements.choices.innerHTML = '';

            choices.forEach((choice, index) => {
                const button = document.createElement('button');
                button.className = 'choice-button';
                button.textContent = choice.text;
                button.onclick = () => handleChoice(choice, currentEvent);
                elements.choices.appendChild(button);
            });
        }

        function handleChoice(choice, event) {
            let consequence = '';

            if (typeof choice.consequence === 'function') {
                consequence = choice.consequence();
            } else if (choice.consequence) {
                consequence = choice.consequence;
            }

            if (consequence) {
                elements.storyText.textContent = speakVictorian(consequence);
            }

            // Generate next event after a delay
            setTimeout(() => {
                if (!gameState.gameEnded) {
                    advanceTime(1);
                    nextEvent();
                }
            }, 2000);
        }

        // Time and Resource Management
        function advanceTime(hours) {
            gameState.day += hours > 24 ? 1 : 0;
            gameState.character.supplies = Math.max(0, gameState.character.supplies - (hours * 2));

            updateResources();

            if (gameState.character.supplies <= 0) {
                gameOver("You have run out of supplies and perished in the streets of Aethermoor.");
            }
        }

        // Quest System
        function startQuest(quest) {
            gameState.currentQuest = quest;
            elements.storyText.textContent = speakVictorian(
                `A most intriguing proposition presents itself: ${quest.description} ` +
                `The reward promises to be ${quest.reward} coins. What say you?`
            );

            presentChoices([
                {
                    text: "Accept this quest",
                    consequence: () => {
                        updateNotification(`Quest accepted: ${quest.name}`);
                        return `Very well! You accept the challenge of ${quest.name}. Time to put your skills to the test.`;
                    }
                },
                {
                    text: "Decline politely",
                    consequence: () => {
                        gameState.currentQuest = null;
                        updateNotification("You decline the quest.");
                        return "Perhaps another time. You have other matters to attend to.";
                    }
                }
            ], { type: 'quest' });
        }

        function completeQuest() {
            const quest = gameState.currentQuest;
            gameState.character.coins += quest.reward;

            Object.entries(quest.statReward).forEach(([stat, amount]) => {
                gameState.character.stats[stat] += amount;
            });

            updateResources();
            updateStats();

            elements.storyText.textContent = speakVictorian(
                `Marvelous work! You have successfully completed "${quest.name}!" ` +
                `The locals speak of your deed with great admiration. You receive ${quest.reward} coins ` +
                `as reward, and your reputation grows considerably.`
            );

            presentChoices([
                {
                    text: "Continue your adventure",
                    consequence: () => {
                        gameState.currentQuest = null;
                        updateNotification("Quest completed!");
                        nextEvent();
                    }
                }
            ], { type: 'questComplete' });
        }

        // Event Types
        function handleQuestEvent(quest) {
            if (!gameState.currentQuest) {
                startQuest(quest);
            } else if (gameState.currentQuest.name === quest.name) {
                completeQuest();
            }
        }

        function handleTradeEvent() {
            const tradeOptions = [
                { item: "Steam Cartridge", cost: 15, description: "Powers small devices" },
                { item: "Clockwork Spring", cost: 8, description: "For repairs and crafting" },
                { item: "Coal Briquette", cost: 5, description: "Fuel for engines" },
                { item: "Magnifying Glass", cost: 12, description: "Better investigation tools" },
                { item: "Fine Tea", cost: 6, description: "Restores spirits" },
                { item: "Navigation Chart", cost: 20, description: "Charts of the sky routes" }
            ];

            const availableTrades = tradeOptions.filter(trade => 
                !gameState.character.inventory.includes(trade.item)
            ).slice(0, 3);

            const tradeText = "A merchant approaches with wares to trade. What takes thy fancy?";
            elements.storyText.textContent = speakVictorian(tradeText);

            const choiceContainer = document.createElement('div');
            availableTrades.forEach(trade => {
                const button = document.createElement('button');
                button.className = 'choice-button';
                button.textContent = `${trade.item} (${trade.cost} coins) - ${trade.description}`;
                button.onclick = () => {
                    if (gameState.character.coins >= trade.cost) {
                        gameState.character.coins -= trade.cost;
                        gameState.character.inventory.push(trade.item);
                        updateResources();
                        updateInventory();
                        updateNotification(`Purchased ${trade.item}!`);
                        nextEvent();
                    } else {
                        updateNotification("Insufficient funds!");
                    }
                };
                choiceContainer.appendChild(button);
            });

            const skipButton = document.createElement('button');
            skipButton.className = 'choice-button';
            skipButton.textContent = "Perhaps later";
            skipButton.onclick = nextEvent;
            choiceContainer.appendChild(skipButton);

            elements.choices.innerHTML = '';
            elements.choices.appendChild(choiceContainer);
        }

        function handleEncounterEvent(encounter) {
            elements.storyText.textContent = speakVictorian(encounter.text);
            presentChoices(encounter.choices, { type: 'encounter' });
        }

        function handleRestEvent() {
            const restChoices = [
                {
                    text: "Stay at an inn (20 coins)",
                    consequence: () => {
                        if (gameState.character.coins >= 20) {
                            gameState.character.coins -= 20;
                            gameState.character.supplies = Math.min(100, gameState.character.supplies + 30);
                            gameState.character.stats.reputation += 1;
                            updateResources();
                            updateStats();
                            updateNotification("A good rest restores your strength!");
                            return "You enjoy a comfortable night's rest at a fine establishment. Your strength and spirits are renewed.";
                        } else {
                            updateNotification("You cannot afford such luxury!");
                            return "Alas, your purse strings are too tight for such comforts.";
                        }
                    }
                },
                {
                    text: "Rest in the streets",
                    consequence: () => {
                        gameState.character.supplies = Math.min(100, gameState.character.supplies + 10);
                        updateResources();
                        updateNotification("A rough rest, but better than nothing.");
                        return "You find a quiet corner and manage some fitful sleep. Not ideal, but it will suffice.";
                    }
                },
                {
                    text: "Push on without rest",
                    consequence: () => {
                        updateNotification("You carry on, though weary.");
                        return "You decide that rest can wait. There is work to be done.";
                    }
                }
            ];

            elements.storyText.textContent = speakVictorian("The day grows long, and weariness settles upon your shoulders. Where might you find rest?");
            presentChoices(restChoices, { type: 'rest' });
        }

        function handleDescriptionEvent(data) {
            elements.storyText.textContent = speakVictorian(data.text);
            presentChoices([
                {
                    text: "Continue your observations",
                    consequence: nextEvent
                },
                {
                    text: "Move to a different location",
                    consequence: () => {
                        gameState.currentLocation = generateLocation();
                        updateLocationDisplay();
                        nextEvent();
                    }
                }
            ], { type: 'description' });
        }

        // Display Updates
        function updateLocationDisplay() {
            const location = gameState.currentLocation;
            elements.locationName.textContent = location.name;
            elements.weatherInfo.textContent = `The air is ${location.weather}`;
            elements.locationDescription.textContent = generateLocationText(location, gameState.character);
        }

        // Main Game Loop
        function nextEvent() {
            if (gameState.gameEnded) return;

            const event = generateEvent();

            switch (event.type) {
                case 'quest':
                    handleQuestEvent(event.data);
                    break;
                case 'trade':
                    handleTradeEvent();
                    break;
                case 'encounter':
                    handleEncounterEvent(event.data);
                    break;
                case 'rest':
                    handleRestEvent();
                    break;
                case 'description':
                    handleDescriptionEvent(event.data);
                    break;
            }
        }

        // Game End
        function gameOver(message) {
            gameState.gameEnded = true;
            elements.gameOverMessage.textContent = speakVictorian(message);

            const stats = gameState.character.stats;
            elements.endStats.innerHTML = `
                <h3 class="section-title">Final Statistics</h3>
                <p><strong>Final Day:</strong> ${gameState.day}</p>
                <p><strong>Final Coins:</strong> ${gameState.character.coins}</p>
                <p><strong>Final Supplies:</strong> ${gameState.character.supplies}%</p>
                <p><strong>Investigation:</strong> ${stats.investigation}</p>
                <p><strong>Engineering:</strong> ${stats.engineering}</p>
                <p><strong>Leadership:</strong> ${stats.leadership}</p>
                <p><strong>Reputation:</strong> ${stats.reputation}</p>
            `;

            showScreen('gameOver');
        }

        function restartGame() {
            gameState = {
                character: null,
                currentLocation: null,
                currentQuest: null,
                day: 1,
                gameEnded: false
            };

            elements.characterName.value = '';
            elements.characterCards.forEach(card => card.classList.remove('selected'));
            elements.startButton.disabled = true;

            showScreen('characterCreation');
        }

        // Event Listeners
        elements.characterName.addEventListener('input', () => {
            updateStartButton();
        });

        elements.characterCards.forEach(card => {
            card.addEventListener('click', () => {
                elements.characterCards.forEach(c => c.classList.remove('selected'));
                card.classList.add('selected');
                updateStartButton();
            });
        });

        function updateStartButton() {
            const hasName = elements.characterName.value.trim().length > 0;
            const hasClass = elements.characterCards.some(card => card.classList.contains('selected'));
            elements.startButton.disabled = !(hasName && hasClass);
        }

        elements.startButton.addEventListener('click', () => {
            const name = elements.characterName.value.trim();
            const classType = document.querySelector('.character-card.selected').dataset.class;

            gameState.character = createCharacter(name, classType);
            gameState.currentLocation = generateLocation();

            // Update UI
            elements.displayName.textContent = name;
            elements.displayClass.textContent = characterClasses[classType].name;
            updateStats();
            updateInventory();
            updateResources();
            updateLocationDisplay();

            showScreen('mainGame');

            // Start first event
            elements.storyText.textContent = speakVictorian(
                `Welcome to Aethermoor, ${name}! The city awaits your contributions. ` +
                `Fate has brought you to ${gameState.currentLocation.name}, where your adventure begins in earnest.`
            );

            setTimeout(nextEvent, 2000);
        });

        // Initialize
        console.log("⚙️ Aethermoor Chronicles loaded! By Jove, what an extraordinary adventure awaits!");
    </script>
</body>
</html>
