    z-index: 1000;
    max-width: 250px;
    box-shadow: 0 4px 15px rgba(0, 0, 0, 0.3);
    display: none;
    border: 2px solid #cd7f32;
}

.tooltip::before {
    content: '';
    position: absolute;
    top: -10px;
    left: 50%;
    transform: translateX(-50%);
    width: 0;
    height: 0;
    border-left: 10px solid transparent;
    border-right: 10px solid transparent;
    border-bottom: 10px solid rgba(44, 24, 16, 0.95);
}

/* Celestial Information Panel */
.info-panel {
    background: rgba(255, 255, 255, 0.9);
    border-radius: 10px;
    padding: 20px;
    margin: 20px 0;
    box-shadow: 0 5px 15px rgba(0, 0, 0, 0.2);
}

.info-grid {
    display: grid;
    grid-template-columns: repeat(auto-fit, minmax(200px, 1fr));
    gap: 15px;
}

.info-item {
    padding: 10px;
    background: rgba(139, 105, 20, 0.1);
    border-radius: 5px;
    border-left: 4px solid #8b6914;
}

.info-label {
    font-family: 'Cinzel', serif;
    color: #8b6914;
    font-size: 0.9em;
    margin-bottom: 5px;
}

.info-value {
    color: #2c1810;
    font-size: 1.1em;
}

/* Animations */
@keyframes rotate {
    from { transform: rotate(0deg); }
    to { transform: rotate(360deg); }
}

@keyframes pulse {
    0%, 100% { transform: scale(1); opacity: 0.8; }
    50% { transform: scale(1.1); opacity: 1; }
}

.rotating {
    animation: rotate 4s linear infinite;
}

.pulsing {
    animation: pulse 2s ease-in-out infinite;
}

/* Responsive Design */
@media (max-width: 768px) {
    .astrolabe {
        width: 400px;
        height: 400px;
    }

    .title {
        font-size: 1.8em;
    }

    .control-grid {
        grid-template-columns: 1fr;
    }
}

/* Star Field Background */
.star-field {
    position: fixed;
    top: 0;
    left: 0;
    width: 100%;
    height: 100%;
    pointer-events: none;
    z-index: -1;
    opacity: 0.6;
}

.star {
    position: absolute;
    width: 2px;
    height: 2px;
    background: white;
    border-radius: 50%;
    animation: twinkle 3s ease-in-out infinite;
}

@keyframes twinkle {
    0%, 100% { opacity: 0.3; }
    50% { opacity: 1; }
}

/* Celestial Bodies */
.celestial-body {
    position: absolute;
    width: 20px;
    height: 20px;
    border-radius: 50%;
    cursor: pointer;
    transition: all 0.3s ease;
    z-index: 50;
}

.celestial-body:hover {
    transform: scale(1.5);
    z-index: 51;
}

.celestial-body.sun { 
    background: radial-gradient(circle, #fff 0%, #ffd700 50%, #ff8c00 100%); 
    box-shadow: 0 0 20px #ffd700;
}

.celestial-body.moon { 
    background: radial-gradient(circle, #fff 0%, #e0e0e0 50%, #c0c0c0 100%); 
    box-shadow: 0 0 15px #e0e0e0;
}

.celestial-body.planet { 
    background: radial-gradient(circle, #a0a0ff 0%, #6a6aff 50%, #4040ff 100%); 
    box-shadow: 0 0 15px #a0a0ff;
}
</style>
</head>
<body>
