</div>

<div class="tooltip" id="tooltip"></div>

<script>
class Astrolabe {
    constructor() {
        this.currentDate = new Date();
        this.latitude = 40.7128;
        this.longitude = -74.0060;
        this.isAnimating = false;
        this.animationSpeed = 1;
        this.astrolabeRotation = 0;

        this.stars = [
            { name: "Polaris", nameEn: "Polaris", constellation: "Ursa Minor", rightAscension: 37.95, declination: 89.26 },
            { name: "Sirius", nameEn: "Sirius", constellation: "Canis Major", rightAscension: 101.29, declination: -16.72 },
            { name: "Betelgeuse", nameEn: "Betelgeuse", constellation: "Orion", rightAscension: 88.79, declination: 7.41 },
            { name: "Rigel", nameEn: "Rigel", constellation: "Orion", rightAscension: 78.63, declination: -8.20 },
            { name: "Aldebaran", nameEn: "Aldebaran", constellation: "Taurus", rightAscension: 68.98, declination: 16.51 },
            { name: "Antares", nameEn: "Antares", constellation: "Scorpius", rightAscension: 247.35, declination: -26.43 },
            { name: "Vega", nameEn: "Vega", constellation: "Lyra", rightAscension: 279.23, declination: 38.78 },
            { name: "Capella", nameEn: "Capella", constellation: "Auriga", rightAscension: 79.17, declination: 45.99 }
        ];

        this.zodiacSigns = [
            "Aries", "Taurus", "Gemini", "Cancer", "Leo", "Virgo",
            "Libra", "Scorpio", "Sagittarius", "Capricorn", "Aquarius", "Pisces"
        ];

        this.houseMeanings = [
            "Self, appearance, and personality",
            "Money, possessions, and values",
            "Communication and immediate environment",
            "Home and family",
            "Creativity and romance",
            "Health and work",
            "Relationships and partnerships",
            "Transformation and shared resources",
            "Philosophy and higher learning",
            "Career and public image",
            "Friendships and community",
            "Dreams and subconscious"
        ];

        this.init();
    }

    init() {
        this.createStarField();
        this.createDegreeMarkings();
        this.setupEventListeners();
        this.updateCelestialPositions();
        this.updateInfoPanel();
        this.animate();
    }

    createStarField() {
        const starField = document.getElementById('starField');
        for (let i = 0; i < 200; i++) {
            const star = document.createElement('div');
            star.className = 'star';
            star.style.left = Math.random() * 100 + '%';
            star.style.top = Math.random() * 100 + '%';
            star.style.animationDelay = Math.random() * 3 + 's';
            starField.appendChild(star);
        }
    }

    createDegreeMarkings() {
        const markingsContainer = document.getElementById('degreeMarkings');
        for (let i = 0; i < 360; i += 5) {
            const mark = document.createElement('div');
            mark.className = 'degree-mark';
            if (i % 30 === 0) {
                mark.classList.add('major');
                mark.title = i + '°';
            }
            mark.style.transform = `rotate(${i}deg) translateY(-280px)`;
            markingsContainer.appendChild(mark);
        }
    }

    setupEventListeners() {
        // Date/Time inputs
        document.getElementById('dateInput').addEventListener('change', (e) => {
            const [year, month, day] = e.target.value.split('-').map(Number);
            this.currentDate.setFullYear(year, month - 1, day);
            this.updateCelestialPositions();
            this.updateInfoPanel();
        });

        document.getElementById('timeInput').addEventListener('change', (e) => {
            const [hours, minutes] = e.target.value.split(':').map(Number);
            this.currentDate.setHours(hours, minutes);
            this.updateCelestialPositions();
            this.updateInfoPanel();
        });

        document.getElementById('latitudeInput').addEventListener('change', (e) => {
            this.latitude = parseFloat(e.target.value);
            this.updateCelestialPositions();
            this.updateInfoPanel();
        });

        document.getElementById('longitudeInput').addEventListener('change', (e) => {
            this.longitude = parseFloat(e.target.value);
            this.updateCelestialPositions();
            this.updateInfoPanel();
        });

        document.getElementById('speedSelect').addEventListener('change', (e) => {
            this.animationSpeed = parseFloat(e.target.value);
        });

        document.getElementById('animateBtn').addEventListener('click', () => {
            this.toggleAnimation();
        });

        document.getElementById('horoscopeBtn').addEventListener('click', () => {
            this.generateHoroscope();
        });

        // Alidade interaction
        const alidade = document.getElementById('alidade');
        let isDragging = false;

        alidade.addEventListener('mousedown', (e) => {
            isDragging = true;
            this.rotateAstrolabe(e);
        });

        document.addEventListener('mousemove', (e) => {
            if (isDragging) {
                this.rotateAstrolabe(e);
            }
        });

        document.addEventListener('mouseup', () => {
            isDragging = false;
        });

        // Zodiac sign tooltips
        document.querySelectorAll('.zodiac-sign').forEach(sign => {
            sign.addEventListener('mouseenter', (e) => {
                this.showTooltip(e, this.getZodiacTooltip(e.target.dataset.sign));
            });

            sign.addEventListener('mouseleave', () => {
                this.hideTooltip();
            });

            sign.addEventListener('mousemove', (e) => {
                this.moveTooltip(e);
            });
        });

        // Central pivot interaction
        document.getElementById('centralPivot').addEventListener('click', () => {
            this.resetAstrolabe();
        });
    }

    rotateAstrolabe(e) {
        const astrolabe = document.getElementById('astrolabe');
        const rect = astrolabe.getBoundingClientRect();
        const centerX = rect.left + rect.width / 2;
        const centerY = rect.top + rect.height / 2;
        const angle = Math.atan2(e.clientY - centerY, e.clientX - centerX);
        const degrees = (angle * 180 / Math.PI + 90 + 360) % 360;

        this.astrolabeRotation = degrees;
        astrolabe.style.transform = `rotate(${degrees}deg)`;

        // Rotate alidade relative to astrolabe
        const alidade = document.getElementById('alidade');
        alidade.style.transform = `rotate(${-degrees}deg)`;

        this.updateCelestialPositions();
    }

    resetAstrolabe() {
        this.astrolabeRotation = 0;
        document.getElementById('astrolabe').style.transform = 'rotate(0deg)';
        document.getElementById('alidade').style.transform = 'rotate(0deg)';
    }

    updateCelestialPositions() {
        // Simplified celestial calculations
        const julianDay = this.getJulianDay();
        const sunPosition = this.calculateSunPosition(julianDay);
        const moonPosition = this.calculateMoonPosition(julianDay);

        // Update celestial bodies display
        this.displayCelestialBody('sun', sunPosition);
        this.displayCelestialBody('moon', moonPosition);

        // Update star positions
        this.updateStarPositions();
    }

    getJulianDay() {
        const date = this.currentDate;
        let a = Math.floor((14 - (date.getMonth() + 1)) / 12);
        let y = date.getFullYear() + 4800 - a;
        let m = (date.getMonth() + 1) + 12 * a - 3;

        return date.getDate() + Math.floor((153 * m + 2) / 5) + 365 * y + 
               Math.floor(y / 4) - Math.floor(y / 100) + Math.floor(y / 400) - 32045 +
               (date.getHours() - 12) / 24 + date.getMinutes() / 1440 + date.getSeconds() / 86400;
    }

    calculateSunPosition(julianDay) {
        const T = (julianDay - 2451545.0) / 36525;
        const L0 = 280.46646 + 36000.76983 * T + 0.0003032 * T * T;
        const M = 357.52911 + 35999.05029 * T - 0.0001537 * T * T;
        const e = 0.016708634 - 0.000042037 * T - 0.0000001267 * T * T;

        const Mrad = M * Math.PI / 180;
        const C = (1.914602 - 0.004817 * T - 0.000014 * T * T) * Math.sin(Mrad) +
                  (0.019993 - 0.000101 * T) * Math.sin(2 * Mrad) +
                  0.000289 * Math.sin(3 * Mrad);

        const trueLongitude = (L0 + C + 180) % 360;
        const Omega = 125.04 - 1934.136 * T;
        const lambda = trueLongitude - 0.00569 - 0.00478 * Math.sin(Omega * Math.PI / 180);

        const epsilon0 = 23 + (26 + (21.448 - T * (46.8150 + T * (0.00059 - 0.001813 * T))) / 60) / 60;
        const epsilon = epsilon0 + 0.00256 * Math.cos(Omega * Math.PI / 180);

        const alpha = Math.atan2(Math.cos(epsilon * Math.PI / 180) * Math.sin(lambda * Math.PI / 180), 
                                Math.cos(lambda * Math.PI / 180)) * 180 / Math.PI;
        const delta = Math.asin(Math.sin(epsilon * Math.PI / 180) * Math.sin(lambda * Math.PI / 180)) * 180 / Math.PI;

        return { rightAscension: (alpha + 360) % 360, declination: delta };
    }

    calculateMoonPosition(julianDay) {
        const T = (julianDay - 2451545.0) / 36525;

        const L0 = 218.316 + 481267.881 * T;
        const D = 297.850 + 445267.111 * T;
        const M = 357.529 + 35999.050 * T;
        const Mp = 134.963 + 477198.868 * T;
        const F = 93.272 + 483202.018 * T;

        const L = (L0 + 6.289 * Math.sin(Mp * Math.PI / 180)) % 360;
        const D_rad = D * Math.PI / 180;
        const L_rad = L * Math.PI / 180;
        const F_rad = F * Math.PI / 180;

        const lat = 5.128 * Math.sin(F_rad) + 0.280 * Math.sin((Mp + F) * Math.PI / 180);
        const lng = L + 6.289 * Math.sin(Mp * Math.PI / 180) - 1.274 * Math.sin((2 * D - Mp) * Math.PI / 180) -
                   0.658 * Math.sin(2 * D * Math.PI / 180) + 0.214 * Math.sin(2 * Mp * Math.PI / 180);

        return { rightAscension: (lng + 360) % 360, declination: lat };
    }

    displayCelestialBody(type, position) {
        const rete = document.getElementById('rete');
        let body = rete.querySelector(`.celestial-body.${type}`);

        if (!body) {
            body = document.createElement('div');
            body.className = `celestial-body ${type}`;
            rete.appendChild(body);
        }

        // Convert celestial coordinates to screen position
        const radius = 200;
        const angle = (position.rightAscension - this.astrolabeRotation) * Math.PI / 180;
        const x = 300 + radius * Math.cos(angle);
        const y = 300 + radius * Math.sin(angle);

        body.style.left = (x - 10) + 'px';
        body.style.top = (y - 10) + 'px';

        body.title = `${type.charAt(0).toUpperCase() + type.slice(1)}: RA ${position.rightAscension.toFixed(2)}°, Dec ${position.declination.toFixed(2)}°`;
    }

    updateStarPositions() {
        const rete = document.getElementById('rete');
        const pointers = rete.querySelectorAll('.pointer.star');

        pointers.forEach((pointer, index) => {
            if (index < this.stars.length) {
                const star = this.stars[index];
                const angle = (star.rightAscension - this.astrolabeRotation) * Math.PI / 180;
                const radius = 150 + Math.sin(index * 0.5) * 30;
                const x = 300 + radius * Math.cos(angle);
                const y = 300 + radius * Math.sin(angle);

                pointer.style.left = (x / 6) + 'px';
                pointer.style.top = (y / 6) + 'px';
                pointer.style.transform = `rotate(${angle * 180 / Math.PI + 90}deg)`;

                pointer.title = `${star.name} (${star.nameEn}) - ${star.constellation}`;
            }
        });
    }

    updateInfoPanel() {
        const infoGrid = document.getElementById('infoGrid');
        const julianDay = this.getJulianDay();
        const sunPos = this.calculateSunPosition(julianDay);
        const moonPos = this.calculateMoonPosition(julianDay);

        const signs = ['Aries', 'Taurus', 'Gemini', 'Cancer', 'Leo', 'Virgo', 
                      'Libra', 'Scorpio', 'Sagittarius', 'Capricorn', 'Aquarius', 'Pisces'];

        const sunSign = signs[Math.floor(sunPos.rightAscension / 30)];
        const moonSign = signs[Math.floor(moonPos.rightAscension / 30)];

        const hourAngle = (sunPos.rightAscension - this.longitude + this.astrolabeRotation) % 360;
        const altitude = Math.asin(Math.sin(this.latitude * Math.PI / 180) * Math.sin(sunPos.declination * Math.PI / 180) +
                                  Math.cos(this.latitude * Math.PI / 180) * Math.cos(sunPos.declination * Math.PI / 180) * 
                                  Math.cos(hourAngle * Math.PI / 180)) * 180 / Math.PI;

        infoGrid.innerHTML = `
            <div class="info-item">
                <div class="info-label">Sun Position</div>
                <div class="info-value">${sunSign} ${(sunPos.rightAscension % 30).toFixed(1)}°</div>
            </div>
            <div class="info-item">
                <div class="info-label">Moon Position</div>
                <div class="info-value">${moonSign} ${(moonPos.rightAscension % 30).toFixed(1)}°</div>
            </div>
            <div class="info-item">
                <div class="info-label">Solar Altitude</div>
                <div class="info-value">${altitude.toFixed(1)}°</div>
            </div>
            <div class="info-item">
                <div class="info-label">Julian Day</div>
                <div class="info-value">${julianDay.toFixed(2)}</div>
            </div>
            <div class="info-item">
                <div class="info-label">Local Sidereal Time</div>
                <div class="info-value">${((hourAngle / 15) % 24).toFixed(2)} hours</div>
            </div>
            <div class="info-item">
                <div class="info-label">Astrolabe Rotation</div>
                <div class="info-value">${this.astrolabeRotation.toFixed(1)}°</div>
            </div>
        `;
    }

    getZodiacTooltip(sign) {
        const tooltips = {
            "Aries": "The Ram - Represents new beginnings, leadership, and pioneering spirit. Associated with Mars.",
            "Taurus": "The Bull - Represents stability, material wealth, and determination. Associated with Venus.",
            "Gemini": "The Twins - Represents communication, curiosity, and versatility. Associated with Mercury.",
            "Cancer": "The Crab - Represents home, family, and emotional security. Associated with the Moon.",
            "Leo": "The Lion - Represents creativity, self-expression, and pride. Associated with the Sun.",
            "Virgo": "The Maiden - Represents health, service, and perfection. Associated with Mercury.",
            "Libra": "The Scales - Represents harmony, balance, and justice. Associated with Venus.",
            "Scorpio": "The Scorpion - Represents transformation, intensity, and depth. Associated with Mars and Pluto.",
            "Sagittarius": "The Archer - Represents philosophy, adventure, and expansion. Associated with Jupiter.",
            "Capricorn": "The Sea Goat - Represents ambition, structure, and authority. Associated with Saturn.",
            "Aquarius": "The Water Bearer - Represents innovation, independence, and humanitarianism. Associated with Saturn and Uranus.",
            "Pisces": "The Fish - Represents intuition, spirituality, and compassion. Associated with Jupiter and Neptune."
        };
        return tooltips[sign] || "";
    }

    showTooltip(event, text) {
        const tooltip = document.getElementById('tooltip');
        tooltip.textContent = text;
        tooltip.style.display = 'block';
        this.moveTooltip(event);
    }

    moveTooltip(event) {
        const tooltip = document.getElementById('tooltip');
        tooltip.style.left = (event.pageX + 10) + 'px';
        tooltip.style.top = (event.pageY + 10) + 'px';
    }

    hideTooltip() {
        document.getElementById('tooltip').style.display = 'none';
    }

    toggleAnimation() {
        const btn = document.getElementById('animateBtn');
        this.isAnimating = !this.isAnimating;

        if (this.isAnimating) {
            btn.textContent = '⏸️ Stop Animation';
            btn.classList.add('pulsing');
        } else {
            btn.textContent = '🌟 Animate Stars';
            btn.classList.remove('pulsing');
        }
    }

    generateHoroscope() {
        const birthDate = new Date(this.currentDate.getTime());
        const latitude = this.latitude;
        const longitude = this.longitude;

        const horoscopeGrid = document.getElementById('horoscopeGrid');
        const display = document.getElementById('horoscopeDisplay');

        let houses = [];
